import React, { useState, useEffect, useRef } from 'react';
import { useParams, useSearchParams, useNavigate } from 'react-router-dom';
import { v4 as uuidv4 } from 'uuid';

const WEBSOCKET_URL = 'ws://localhost:8080/ws';

function MeetingRoom() {
  const { roomId } = useParams();
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();

  const username = searchParams.get('username');
  const userId = useRef(uuidv4());

  const [localStream, setLocalStream] = useState(null);
  const [remoteStreams, setRemoteStreams] = useState(new Map());
  const [users, setUsers] = useState([]);
  const [isAudioEnabled, setIsAudioEnabled] = useState(true);
  const [isVideoEnabled, setIsVideoEnabled] = useState(true);
  const [isConnected, setIsConnected] = useState(false);
  const [error, setError] = useState('');

  const localVideoRef = useRef();
  const websocketRef = useRef();
  const peerConnectionsRef = useRef(new Map());
  const remoteVideoRefs = useRef(new Map());

  const servers = {
    iceServers: [
      { urls: 'stun:stun.l.google.com:19302' },
      { urls: 'stun:stun1.l.google.com:19302' }
    ]
  };

  useEffect(() => {
    if (!username) {
      navigate('/');
      return;
    }

    initializeMedia();
    connectWebSocket();

    return () => {
      cleanup();
    };
  }, [roomId, username]);

  const initializeMedia = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true
      });
      setLocalStream(stream);
      if (localVideoRef.current) {
        localVideoRef.current.srcObject = stream;
      }
    } catch (err) {
      console.error('Ëé∑ÂèñÂ™í‰ΩìËÆæÂ§áÂ§±Ë¥•:', err);
      setError('Êó†Ê≥ïËÆøÈóÆÊëÑÂÉèÂ§¥ÊàñÈ∫¶ÂÖãÈ£éÔºåËØ∑Ê£ÄÊü•ËÆæÂ§áÊùÉÈôê');
    }
  };

  const connectWebSocket = () => {
    const wsUrl = `${WEBSOCKET_URL}?userId=${userId.current}&roomId=${roomId}&username=${encodeURIComponent(username)}`;
    websocketRef.current = new WebSocket(wsUrl);

    websocketRef.current.onopen = () => {
      console.log('WebSocketËøûÊé•Â∑≤Âª∫Á´ã');
      setIsConnected(true);
    };

    websocketRef.current.onmessage = async (event) => {
      const message = JSON.parse(event.data);
      await handleWebSocketMessage(message);
    };

    websocketRef.current.onclose = () => {
      console.log('WebSocketËøûÊé•Â∑≤ÂÖ≥Èó≠');
      setIsConnected(false);
    };

    websocketRef.current.onerror = (error) => {
      console.error('WebSocketÈîôËØØ:', error);
      setError('ÊúçÂä°Âô®ËøûÊé•Â§±Ë¥•');
    };
  };

  const handleWebSocketMessage = async (message) => {
    console.log('Êî∂Âà∞Ê∂àÊÅØ:', message);

    switch (message.type) {
      case 'user-list':
        setUsers(message.data || []);
        break;

      case 'user-joined':
        if (message.userId !== userId.current) {
          setUsers(prev => [...prev, { id: message.userId, username: message.username }]);
          await createPeerConnection(message.userId);
          await createOffer(message.userId);
        }
        break;

      case 'user-left':
        setUsers(prev => prev.filter(user => user.id !== message.userId));
        closePeerConnection(message.userId);
        break;

      case 'offer':
        await handleOffer(message);
        break;

      case 'answer':
        await handleAnswer(message);
        break;

      case 'ice-candidate':
        await handleIceCandidate(message);
        break;
    }
  };

  const createPeerConnection = async (remoteUserId) => {
    const peerConnection = new RTCPeerConnection(servers);
    peerConnectionsRef.current.set(remoteUserId, peerConnection);

    // Ê∑ªÂä†Êú¨Âú∞ÊµÅ
    if (localStream) {
      localStream.getTracks().forEach(track => {
        peerConnection.addTrack(track, localStream);
      });
    }

    // Â§ÑÁêÜËøúÁ®ãÊµÅ
    peerConnection.ontrack = (event) => {
      console.log('Êî∂Âà∞ËøúÁ®ãÊµÅ:', event);
      const [remoteStream] = event.streams;
      setRemoteStreams(prev => new Map(prev.set(remoteUserId, remoteStream)));
    };

    // Â§ÑÁêÜICEÂÄôÈÄâËÄÖ
    peerConnection.onicecandidate = (event) => {
      if (event.candidate) {
        sendMessage({
          type: 'ice-candidate',
          data: event.candidate,
          targetUserId: remoteUserId
        });
      }
    };

    return peerConnection;
  };

  const createOffer = async (remoteUserId) => {
    const peerConnection = peerConnectionsRef.current.get(remoteUserId);
    if (!peerConnection) return;

    try {
      const offer = await peerConnection.createOffer();
      await peerConnection.setLocalDescription(offer);

      sendMessage({
        type: 'offer',
        data: offer,
        targetUserId: remoteUserId
      });
    } catch (error) {
      console.error('ÂàõÂª∫offerÂ§±Ë¥•:', error);
    }
  };

  const handleOffer = async (message) => {
    const { userId: remoteUserId, data: offer } = message;

    let peerConnection = peerConnectionsRef.current.get(remoteUserId);
    if (!peerConnection) {
      peerConnection = await createPeerConnection(remoteUserId);
    }

    try {
      await peerConnection.setRemoteDescription(offer);
      const answer = await peerConnection.createAnswer();
      await peerConnection.setLocalDescription(answer);

      sendMessage({
        type: 'answer',
        data: answer,
        targetUserId: remoteUserId
      });
    } catch (error) {
      console.error('Â§ÑÁêÜofferÂ§±Ë¥•:', error);
    }
  };

  const handleAnswer = async (message) => {
    const { userId: remoteUserId, data: answer } = message;
    const peerConnection = peerConnectionsRef.current.get(remoteUserId);

    if (peerConnection) {
      try {
        await peerConnection.setRemoteDescription(answer);
      } catch (error) {
        console.error('Â§ÑÁêÜanswerÂ§±Ë¥•:', error);
      }
    }
  };

  const handleIceCandidate = async (message) => {
    const { userId: remoteUserId, data: candidate } = message;
    const peerConnection = peerConnectionsRef.current.get(remoteUserId);

    if (peerConnection) {
      try {
        await peerConnection.addIceCandidate(candidate);
      } catch (error) {
        console.error('Ê∑ªÂä†ICEÂÄôÈÄâËÄÖÂ§±Ë¥•:', error);
      }
    }
  };

  const sendMessage = (message) => {
    if (websocketRef.current && websocketRef.current.readyState === WebSocket.OPEN) {
      websocketRef.current.send(JSON.stringify(message));
    }
  };

  const closePeerConnection = (remoteUserId) => {
    const peerConnection = peerConnectionsRef.current.get(remoteUserId);
    if (peerConnection) {
      peerConnection.close();
      peerConnectionsRef.current.delete(remoteUserId);
    }
    setRemoteStreams(prev => {
      const newMap = new Map(prev);
      newMap.delete(remoteUserId);
      return newMap;
    });
  };

  const toggleAudio = () => {
    if (localStream) {
      const audioTrack = localStream.getAudioTracks()[0];
      if (audioTrack) {
        audioTrack.enabled = !audioTrack.enabled;
        setIsAudioEnabled(audioTrack.enabled);
      }
    }
  };

  const toggleVideo = () => {
    if (localStream) {
      const videoTrack = localStream.getVideoTracks()[0];
      if (videoTrack) {
        videoTrack.enabled = !videoTrack.enabled;
        setIsVideoEnabled(videoTrack.enabled);
      }
    }
  };

  const leaveRoom = () => {
    cleanup();
    navigate('/');
  };

  const cleanup = () => {
    if (localStream) {
      localStream.getTracks().forEach(track => track.stop());
    }

    peerConnectionsRef.current.forEach(pc => pc.close());
    peerConnectionsRef.current.clear();

    if (websocketRef.current) {
      websocketRef.current.close();
    }
  };

  const copyRoomId = () => {
    navigator.clipboard.writeText(roomId);
